<?php\n$author = $_SESSION['username'];\n$subject = $_POST['subject'];\n$body = $_POST['body'];\n$time = time();\n$branchedFrom = $_POST['parent'];\n$id = $_POST['parent']; // This seems redundant, you might want to clarify its use\n$next = 0;\n$previous = 0;\n$branchedTo = 0;\n\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_dbname', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->prepare("INSERT INTO offtopic (author, subject, body, time, next, previous, branchedFrom, branchedTo) VALUES (:author, :subject, :body, :time, :next, :previous, :branchedFrom, :branchedTo)");\n    $stmt->execute([\n        ':author' => $author,\n        ':subject' => $subject,\n        ':body' => $body,\n        ':time' => $time,\n        ':next' => $next,\n        ':previous' => $previous,\n        ':branchedFrom' => $branchedFrom,\n        ':branchedTo' => $branchedTo\n    ]);\n\n    echo "Data inserted successfully";\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>