// Assuming $mysqli is your MySQLi connection\n$stmt = $mysqli->prepare("SELECT id, name, email FROM users");\n$stmt->execute();\n\n// Store the result (this is necessary for fetching meta data later)\n$result = $stmt->store_result();\n\n// Get field information for all columns\n$meta = $result->fetch_fields();\n\n// Dynamically create variables based on column names and bind them to the statement\n$parameters = [];\nforeach ($meta as $field) {\n    $parameters[] = &$row[$field->name];\n}\n\ncall_user_func_array(array($stmt, 'bind_result'), $parameters);\n\n// Fetch values\nwhile ($stmt->fetch()) {\n    // Use array_map with null to force getting variables by reference and reassign them to a new array\n    $outputArray = array_map(function (&$value) { return $value; }, $row);\n    \n    // Now $outputArray holds the data as an associative array\n    print_r($outputArray);\n}\n\n$stmt->close();