<?php\n\nfunction getSpanContentByLang($html, $lang) {\n    // Define a pattern to match span content by language attribute\n    $pattern = '/<span lang="' . preg_quote($lang, '/') . '">(.*?)<\/span>/is';\n    \n    // Use preg_match_all to find all matches\n    if (preg_match_all($pattern, $html, $matches)) {\n        return $matches[1]; // Return the captured content of the span\n    }\n    \n    return []; // Return an empty array if no matches are found\n}\n\n$html = '<span lang="en">English Content</span><span lang="fr">French content</span> ... etc ...';\n$language = 'fr';\n$result = getSpanContentByLang($html, $language);\n\nprint_r($result); // Output: Array ( [0] => French content )\n\n?>