<?php\n\nfunction getSpanContentByLangUsingDOM($html, $lang) {\n    $dom = new DOMDocument();\n    @$dom->loadHTML(utf8_decode($html)); // @ to suppress warnings\n    \n    $xpath = new DOMXPath($dom);\n    $nodes = $xpath->query("//span[@lang='$lang']");\n    \n    $contents = [];\n    foreach ($nodes as $node) {\n        $contents[] = trim($node->textContent);\n    }\n    \n    return $contents;\n}\n\n$html = '<span lang="en">English Content</span><span lang="fr">French content</span> ... etc ...';\n$language = 'fr';\n$result = getSpanContentByLangUsingDOM($html, $language);\n\nprint_r($result); // Output: Array ( [0] => French content )\n\n?>