function sanitizeFileName($fileName) {\n    // Remove any character that is not word character, digit, hyphen, underscore, or space\n    $sanitized = preg_replace('/[^a-z0-9_-]/i', '', strtolower(trim($fileName)));\n    // Replace multiple underscores with a single underscore\n    $sanitized = preg_replace('/_{2,}/', '_', $sanitized);\n    return $sanitized;\n}\n\n// Example usage\n$originalName = "user file & name.txt";\n$safeFileName = sanitizeFileName($originalName);\necho $safeFileName;  // Output: user_file_name_txt