<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $option = isset($_POST['option']) ? $_POST['option'] : '';\n    $otherText = isset($_POST['option_text']) ? $_POST['option_text'] : '';\n\n    if ($option === 'Other') {\n        echo "You selected: Other - " . htmlspecialchars($otherText);\n    } else {\n        echo "You selected: " . htmlspecialchars($option);\n    }\n}\n?>