<?php\nfunction createHash($senderId, $recipientId, $secretKey) {\n    // Concatenate IDs with a secret key for additional obfuscation\n    $data = $senderId . ':' . $recipientId . ':' . $secretKey;\n    // Create a simple MD5 hash of the data\n    $hash = md5($data);\n    // Base64 encode the hash to keep it URL-friendly and short\n    return substr(base64_encode($hash), 0, 12); // Shorten the hash for brevity\n}\n\nfunction decodeHash($hash, $senderId, $recipientId, $secretKey) {\n    $fullHash = createHash($senderId, $recipientId, $secretKey);\n    return substr(base64_encode(md5($senderId . ':' . $recipientId . ':' . $secretKey)), 0, 12) === $hash;\n}\n\n// Example usage\n$senderId = 123;\n$recipientId = 456;\n$secretKey = 'mySecret';\n\n$hash = createHash($senderId, $recipientId, $secretKey);\necho "Hash: " . $hash . "\n";\n\nif (decodeHash($hash, $senderId, $recipientId, $secretKey)) {\n    echo "Hash verified successfully.";\n} else {\n    echo "Hash verification failed.";\n}\n?>