<?php\n// Initialize cURL session\n$ch = curl_init();\n\n// Set URL to send the POST request to\ncurl_setopt($ch, CURLOPT_URL, "https://example.com/someendpoint");\n\n// Enable SSL certificate verification (recommended)\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);\n\n// Setup the authentication headers using username and password\n$credentials = 'username:password';\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: Basic ' . base64_encode($credentials)));\n\n// Set POST method and data to be sent\n$postData = "cmd=_initiate_query";\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $postData);\n\n// Return the transfer as a string instead of outputting it directly\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute the cURL session and store the response\n$response = curl_exec($ch);\n\n// Check for errors in the request\nif (curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    echo $response;\n}\n\n// Close the cURL session\ncurl_close($ch);\n?>