<?php\n// Database connection settings (use environment variables for security)\n$host = "localhost";\n$dbname = "your_database_name";\n$username = getenv('DB_USERNAME');\n$password = getenv('DB_PASSWORD');\n\n// Create connection\n$conn = new mysqli($host, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Insert geological data into the database\nfunction insertData($stmt, $streetName, $latitude, $longitude) {\n    $stmt->bind_param("ssdd", $userId, $streetName, $latitude, $longitude);\n    \n    // Assuming user_id is already known and sanitized\n    $userId = 1; // Example user ID\n\n    // Execute statement\n    if ($stmt->execute()) {\n        echo "Data inserted successfully";\n    } else {\n        echo "Error: " . $stmt->error;\n    }\n}\n\n// Function to retrieve data from the database\nfunction fetchData($conn) {\n    $sql = "SELECT street_name, latitude, longitude FROM geological_data";\n    $result = $conn->query($sql);\n\n    if ($result->num_rows > 0) {\n        $data = [];\n        while($row = $result->fetch_assoc()) {\n            $data[] = $row;\n        }\n        return $data; // Return the data array to be used by JavaScript\n    } else {\n        echo "No data found";\n        return [];\n    }\n}\n\n// Prepare insert statement\n$insertStmt = $conn->prepare("INSERT INTO geological_data (user_id, street_name, latitude, longitude) VALUES (?, ?, ?, ?)");\n\n// Example usage: Insert data\ninsertData($insertStmt, "Example Street", 37.4219999, -122.0840575);\n\n// Fetch and output JSON data for Google Maps\necho json_encode(fetchData($conn));\n\n// Close connection\n$conn->close();\n?>