<?php\nif(isset($_GET['monthlybill'])) {\n    $monthlybill = $_GET['monthlybill'];\n    \n    // Ensure that monthly bill is numeric and positive\n    if(is_numeric($monthlybill) && $monthlybill > 0) {\n        echo "<p>Your monthly bill was $" . htmlspecialchars($monthlybill) . "</p>";\n\n        $re = 1;\n        while ($re <= 7) { // Assuming you have 7 offers defined\n            $offername = 'offer' . $re . 'name';\n            $offerlogo = 'offer' . $re . 'logo';\n            $offerlink = 'offer' . $re . 'link';\n            $offeranchor = 'offer' . $re . 'anchor';\n            $offerstar = 'offer' . $re . 'star1';\n            $offerbottomline = 'offer' . $re . 'bottomline';\n            $offercalcsavings = 'offer' . $re . 'calcsavings';\n\n            // Assuming these variables are set in values.php which should be included\n            if(isset($$offername)) {\n                echo '<tr>\n                        <td>\n                            <a href="' . htmlspecialchars($$offerlink) . '" target="blank">\n                                <img src="http://www.nextadvisor.com' . htmlspecialchars($$offerlogo) . '" alt="' . htmlspecialchars($$offername) . '" />\n                            </a>\n                        </td>\n                        <td>\n                            <span class="rating_text">Rating:</span>\n                            <span class="star_rating1">\n                                <img src="IMAGE' . htmlspecialchars($$offerstar) . '" alt="" />\n                            </span>\n                            <br />\n                            <div style="margin-top:5px; color:#0000FF;">\n                                <a href="' . htmlspecialchars($$offerlink) . '" target="blank">Go to Site</a>\n                                <span style="margin:0px 7px 0px 7px;">|</span>\n                                <a href="' . htmlspecialchars($$offeranchor) . '">Review</a>\n                            </div>\n                        </td>\n                        <td >';\n                \n                // Calculate savings\n                $annualSavings = ($monthlybill * 12) - (12 * $$offercalcsavings);\n                echo '$' . number_format($annualSavings, 2) . '</td>  \n                    </tr>';\n            }\n\n            $re++;\n        }\n    } else {\n        echo "<p>Please enter a valid monthly phone bill.</p>";\n    }\n} else {\n    echo "<p>No input detected. Please submit your monthly bill.</p>";\n}\n?>