function sanitizeUserInput($input) {\n    // Define allowed tags: e.g., <b>, <i>\n    $allowedTags = '<b><i><u>';\n    \n    // Strip out disallowed tags but keep the allowed ones\n    $sanitizedInput = strip_tags($input, $allowedTags);\n    \n    // Convert special characters to HTML entities (except for allowed tags)\n    $safeOutput = htmlspecialchars($sanitizedInput, ENT_NOQUOTES, 'UTF-8');\n    \n    return $safeOutput;\n}\n\n// Example usage\n$userInput = '<b> 5 > 3 = true</b>';\n$safeToDisplay = sanitizeUserInput($userInput);\necho $safeToDisplay; // Outputs: <strong>5 &gt; 3 = true</strong>