// Set the default timezone (example: UTC)\ndate_default_timezone_set('UTC');\n\n$date = '2009-01-01 10:30:00';\n\n// Create a Zend_Date instance in UTC\n$dateObject = new Zend_Date($date, Zend_Date::ISO_8601);\n\n// User's timezone (example: Australia/ACT)\n$userTimezone = 'Australia/ACT';\n\n// Convert and display the date in user's timezone\n$dateObject->setTimezone($userTimezone);\necho $dateObject->get(Zend_Date::TIME_MEDIUM); // Outputs time converted to user's timezone\n\n// Function to convert any date to user's timezone\nfunction convertToUserTimezone(Zend_Date $date, $timezone) {\n    return $date->setTimezone($timezone)->toString('H:m:s');\n}\n\n// Usage of the function\necho convertToUserTimezone(new Zend_Date($date, Zend_Date::ISO_8601), 'Australia/ACT'); // Outputs: 21:30:00