<?php\n// Create a new WScript.Shell object via COM\n$shell = new COM("WScript.Shell");\n\n// Define the shortcut path and target\n$linkPath = "C:\\Path\\To\\Shortcut.lnk";\n$targetPath = "C:\\Path\\To\\Target";\n\n// Create a shortcut pointer\n$shortcut = $shell->CreateShortcut($linkPath);\n\n// Set required properties of the shortcut:\n$shortcut->Targetpath = $targetPath;\n\n// The working directory for when you use the shortcut\n$shortcut->WorkingDirectory = dirname($targetPath);\n\n// Optional window state after running the shortcut (normal, minimized or maximized)\n$shortcut->WindowStyle = 7;\n\n// Save the created shortcut\n$shortcut->Save();\n?>