function cartesianProduct($arrays) {\n    $result = array(array());\n    \n    foreach ($arrays as $property => $values) {\n        $tmp = array();\n        \n        foreach ($result as $resultItem) {\n            foreach ($values as $item) {\n                $tmp[] = array_merge($resultItem, array($property => $item));\n            }\n        }\n        \n        $result = $tmp;\n    }\n    \n    return $result;\n}\n\n$sizes =   array(\n    'Shirt Size' => array('Small', 'Medium', 'Large'),\n    'Sleeve Size' => array('Short', 'Long')\n);\n\n$colors =  array(\n    'Shirt Color' => array('Black', 'Red'),\n    'Sleeve Color' => array('White', 'Orange')\n);\n\n// Merge the two attribute arrays\n$attributes = array_merge($sizes, $colors);\n\n// Generate all possible combinations\n$productCombinations = cartesianProduct($attributes);\n\n// Output the results in a table-like format\necho "SHIRT SIZE\tSLEEVE SIZE\tSHIRT COLOR\tSLEEVE COLOR\n";\nforeach ($productCombinations as $combination) {\n    echo implode("\t", $combination) . "\n";\n}