<?php\n\n   // Include the autoloader from Composer\n   require 'vendor/autoload.php';\n\n   use PhpExtras\ExpressionParser\ExpressionParser;\n\n   // Create a new instance of ExpressionParser\n   $parser = new ExpressionParser();\n\n   // Form field values\n   $formData = [\n       'firstname' => '',\n       'lastname' => ''\n   ];\n\n   // User-defined logical expression\n   $expression = "firstname == '' or lastname == ''";\n\n   try {\n       // Evaluate the expression with the form data\n       $result = $parser->evaluate($expression, $formData);\n       \n       if ($result) {\n           echo 'The condition is true.';\n       } else {\n           echo 'The condition is false.';\n       }\n   } catch (\Exception $e) {\n       echo 'An error occurred: ' . $e->getMessage();\n   }