<?php\n\n// Database connection settings\n$dsn = 'oci:dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n} catch (PDOException $e) {\n    echo "Connection failed: " . $e->getMessage();\n    exit;\n}\n\n// SQL to get the date ranges from the database\n$sql = "SELECT start_date, end_date FROM your_table";\n$stmt = $pdo->query($sql);\n\n// Array to store counts of each day\n$dayCounts = [];\n\nwhile ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n    $startDate = new DateTime($row['START_DATE']);\n    $endDate = new DateTime($row['END_DATE']);\n\n    // Loop from start_date to end_date\n    for ($date = $startDate; $date <= $endDate; $date->modify('+1 day')) {\n        $formattedDate = $date->format('jS F');\n        if (!isset($dayCounts[$formattedDate])) {\n            $dayCounts[$formattedDate] = 0;\n        }\n        $dayCounts[$formattedDate]++;\n    }\n}\n\n// Sort the results by date\nksort($dayCounts);\n\n// Print out the results\nforeach ($dayCounts as $date => $count) {\n    echo "$date: $count\n";\n}\n\n?>