# build query to fetch search results\n$c = new Criteria();\n\n$priceFrom = $request->getPostParameter('price_from');\n$priceTo = $request->getPostParameter('price_to');\n\nif ($priceFrom > 0 || $priceTo > 0) {\n    $nestedCriteria = $c->getNewCriterion(HomeModelPeer::PRICE, null);\n\n    if ($priceFrom > 0) {\n        $nestedCriteria->addAnd($c->getNewCriterion(HomeModelPeer::PRICE, $priceFrom, Criteria::GREATER_EQUAL));\n    }\n\n    if ($priceTo > 0) {\n        $nestedCriteria->addAnd($c->getNewCriterion(HomeModelPeer::PRICE, $priceTo, Criteria::LESS_EQUAL));\n    }\n\n    $c->add($nestedCriteria);\n}