<?php\n// store_data.php\n\n// Get data from AJAX POST request and decode it from JSON\n$data = json_decode($_POST['data_to_store'], true);\n\nif ($data) {\n    // Database connection variables (update these with your database credentials)\n    $servername = "localhost";\n    $username = "your_username";\n    $password = "your_password";\n    $dbname = "your_database";\n\n    try {\n        // Create a new PDO instance to connect to the database\n        $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\n        // Set the PDO error mode to exception to handle errors better\n        $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Prepare an INSERT statement for data (assuming you have a table called `data` with columns matching the keys in your data)\n        $stmt = $conn->prepare("INSERT INTO data (column1, column2) VALUES (:value1, :value2)");\n\n        // Bind parameters to the SQL statement (update these based on your actual table structure and data)\n        $stmt->bindParam(':value1', $data['key1']);\n        $stmt->bindParam(':value2', $data['key2']);\n\n        // Execute the statement\n        $stmt->execute();\n\n        echo "Data successfully stored!";\n    } catch (PDOException $e) {\n        echo "Error: " . $e->getMessage();\n    }\n\n    // Close the connection\n    $conn = null;\n} else {\n    echo "Invalid or empty data received.";\n}\n?>