// Initialize cURL session\n  $ch = curl_init();\n  \n  // Set headers and URL for the token request\n  $url = "https://login.microsoftonline.com/common/oauth2/v2.0/token";\n  $fields = [\n      'client_id' => 'YOUR_CLIENT_ID',\n      'scope' => 'https://graph.microsoft.com/Contacts.Read',\n      'code' => 'AUTHORIZATION_CODE',\n      'redirect_uri' => 'REDIRECT_URI',\n      'grant_type' => 'authorization_code',\n      'client_secret' => 'CLIENT_SECRET'\n  ];\n  \n  curl_setopt($ch, CURLOPT_URL, $url);\n  curl_setopt($ch, CURLOPT_POST, true);\n  curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($fields));\n  curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n  \n  // Execute cURL session\n  $response = json_decode(curl_exec($ch), true);\n  $access_token = $response['access_token'];\n  \n  // Close cURL session\n  curl_close($ch);\n  \n  // Fetch contacts using the access token\n  $headers = [\n      'Authorization: Bearer ' . $access_token,\n      'Content-Type: application/json'\n  ];\n  \n  $urlContacts = "https://graph.microsoft.com/v1.0/me/contacts";\n  $ch_contacts = curl_init();\n  curl_setopt($ch_contacts, CURLOPT_URL, $urlContacts);\n  curl_setopt($ch_contacts, CURLOPT_HTTPHEADER, $headers);\n  curl_setopt($ch_contacts, CURLOPT_RETURNTRANSFER, true);\n  \n  // Execute and close\n  $contacts_response = json_decode(curl_exec($ch_contacts), true);\n  curl_close($ch_contacts);\n  \n  print_r($contacts_response);