class Example {\n    public $property = null;\n}\n\n$obj = new Example();\n\nif (property_exists($obj, 'property')) {\n    echo "Property 'property' exists.\n";\n} else {\n    echo "Property 'property' does not exist.\n";\n}\n\nif (property_exists($obj, 'nonExistentProperty')) {\n    echo "Property 'nonExistentProperty' exists.\n";\n} else {\n    echo "Property 'nonExistentProperty' does not exist.\n";\n}