<?php\n\nfunction fetchAndExtractUlContent($url) {\n    // Initialize cURL session\n    $ch = curl_init();\n\n    // Set URL and other appropriate options\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n    // Execute cURL request and get the HTML response\n    $htmlContent = curl_exec($ch);\n\n    // Check for errors in the cURL execution\n    if ($htmlContent === false) {\n        echo 'cURL Error: ' . curl_error($ch);\n        return '';\n    }\n\n    // Close the cURL session\n    curl_close($ch);\n\n    // Load HTML into DOMDocument\n    $DOM = new DOMDocument();\n    @$DOM->loadHTML($htmlContent); // Use @ to suppress warnings due to possibly malformed HTML\n\n    // Create a DOMXPath object to query the document\n    $xpath = new DOMXPath($DOM);\n    \n    // Query for <ul> elements\n    $uls = $xpath->query('//ul');\n    \n    // Collect and return the <ul> contents\n    $ulContents = '';\n    foreach ($uls as $ul) {\n        $ulContents .= $DOM->saveHTML($ul);\n    }\n\n    return $ulContents;\n}\n\n// URLs to fetch content from\n$tracUrl = 'http://myserver/trac';\n$svnUrl = 'http://myserver/svn';\n\n// Fetch and extract <ul> contents\n$tracUlContent = fetchAndExtractUlContent($tracUrl);\n$svnUlContent = fetchAndExtractUlContent($svnUrl);\n\n// Render the extracted <ul> contents on index.php\necho '<h2>TRAC Content</h2>';\necho $tracUlContent;\n\necho '<h2>SVN Content</h2>';\necho $svnUlContent;\n?>