<?php\nheader('Content-Type: text/plain; charset=utf-8');\n\n// Database connection settings - replace with your actual database credentials\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_dbname";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Retrieve the ID sent from AS3\n$id = intval($_POST['id']);\n\n// Prepare and bind to avoid SQL injection\n$stmt = $conn->prepare("SELECT your_column FROM your_table WHERE id = ?");\n$stmt->bind_param("i", $id);\n\n// Execute statement\n$stmt->execute();\n$stmt->bind_result($value);\n$stmt->fetch();\n\n// Return the fetched value back to AS3\necho $value;\n\n// Close all connections\n$stmt->close();\n$conn->close();\n?>