class Example {\n    private $items = [];\n\n    public function addItem($item) {\n        // Ensure items is treated as an empty array\n        if (empty($this->items)) {\n            $this->items = [];\n        }\n        $this->items[] = $item;\n    }\n\n    public function getItems() {\n        return $this->items;\n    }\n}\n\n$example = new Example();\n$example->addItem('new item');\nprint_r($example->getItems()); // Outputs: Array ( [0] => new item )