<?php\n\n$hostnames = [\n    'google.com',\n    'facebook.com',\n    'twitter.com',\n    'github.com',\n    'microsoft.com'\n];\n\n$dnsCheckResults = [];\n\nforeach ($hostnames as $hostname) {\n    $ipAddress = gethostbyname($hostname);\n    if (filter_var($ipAddress, FILTER_VALIDATE_IP)) {\n        $dnsCheckResults[$hostname] = 'Success';\n    } else {\n        $dnsCheckResults[$hostname] = 'Failed';\n    }\n}\n\necho "<h1>DNS Lookup Results</h1>";\nforeach ($dnsCheckResults as $hostname => $result) {\n    echo "$hostname: $result<br>";\n}\n?>