<?php\nfunction checkHostReachability($host) {\n    $port = 80; // or 443 for HTTPS\n    $timeout = 5;\n    \n    // Attempt to connect on the specified port\n    $connection = @fsockopen($host, $port, $errno, $errstr, $timeout);\n    \n    if ($connection) {\n        fclose($connection);\n        return true; // Host is reachable\n    } else {\n        return false; // Host is not reachable\n    }\n}\n\n$host = "example.com"; // Replace with your target host\nif (checkHostReachability($host)) {\n    echo $host . " is reachable.";\n} else {\n    echo $host . " is NOT reachable.";\n}\n?>