<?PHP\n$countries = array(\n    "224" => "United States",\n    "223" => "United Kingdom (Great Britain)",\n    "217" => "Turkmenistan",\n    "218" => "Turks and Caicos Islands",\n    "219" => "Tuvalu",\n    "220" => "Uganda",\n    "221" => "Ukraine",\n    "222" => "United Arab Emirates"\n);\n\n$mycountry = 224;\n?>\n\n<select name="country" style="width:180px;" onChange="do_get_rest_popup(this.value)">\n<?php\nforeach ($countries as $id => $c) {\n    $_select = ($id == $mycountry) ? 'SELECTED' : '';\n    echo "<option value=\"$id\" $_select>$c</option>";\n}\n?>\n</select>