try {\n    $select = $this->_db->select()\n         ->from('OSLANG', 'oslang') // Select only the oslang column from OSLANG table\n         ->join('OS', 'OSLANG.OSLANG_ID = OS.OSLANG_ID', array()) // Join OS table on condition\n         ->where('OS.OSSUBVERSION_ID = ?', $subVersionId);\n    $results = $select->query()->fetchAll();\n} catch (Exception $e) {\n    $this->_logException($e);\n    $results = array();\n}