<?php\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set URL\n$url = 'http://example.com/form-page';\n\n// Set options\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Execute and fetch the HTML content\n$htmlContent = curl_exec($ch);\n\n// Close cURL session\ncurl_close($ch);\n\n// Create a DOMDocument object\n$dom = new DOMDocument();\n@$dom->loadHTML($htmlContent); // Suppress warnings with '@'\n\n// Initialize a DOMXPath object\n$xpath = new DOMXPath($dom);\n\n// Find the form element you are interested in. Adjust the XPath query if necessary.\n$form = $xpath->query('//form[@id="your-form-id"]')->item(0);\n\n// Check if we found a form\nif ($form) {\n    // Initialize variables for form fields and their values\n    $fields = [];\n\n    // Extract all input elements within the form\n    foreach ($xpath->query('.//input', $form) as $node) {\n        if ($node->getAttribute('name') && !in_array($node->getAttribute('type'), ['submit', 'button'])) {\n            $fields[$node->getAttribute('name')] = $node->getAttribute('value');\n        }\n    }\n\n    // Now you can modify the fields as needed\n    // For example, change a specific field value:\n    if (isset($fields['some_field_name'])) {\n        $fields['some_field_name'] = 'new_value';\n    }\n\n    // Build POST data string\n    $postData = http_build_query($fields);\n\n    // Initialize another cURL session to send the POST request\n    $ch = curl_init();\n\n    // Set URL\n    curl_setopt($ch, CURLOPT_URL, 'http://example.com/form-submit-page');\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch, CURLOPT_POST, true);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);\n\n    // Execute and fetch the result of the POST request\n    $result = curl_exec($ch);\n\n    // Close cURL session\n    curl_close($ch);\n\n    // Output the result\n    echo $result;\n\n} else {\n    die('Form not found.');\n}\n\n?>