<?php\n\nfunction xorEncrypt($string, $key) {\n    $encryptedString = '';\n    $keyLength = strlen($key);\n    for ($i = 0; $i < strlen($string); $i++) {\n        $char = substr($string, $i, 1);\n        $encryptedChar = chr(ord($char) ^ ord($key[$i % $keyLength]));\n        $encryptedString .= $encryptedChar;\n    }\n    return base64_encode($encryptedString);\n}\n\nfunction xorDecrypt($string, $key) {\n    $decryptedString = '';\n    $string = base64_decode($string);\n    $keyLength = strlen($key);\n    for ($i = 0; $i < strlen($string); $i++) {\n        $char = substr($string, $i, 1);\n        $decryptedChar = chr(ord($char) ^ ord($key[$i % $keyLength]));\n        $decryptedString .= $decryptedChar;\n    }\n    return $decryptedString;\n}\n\n// Example usage\n$petVariationID = "03"; // Assuming variation ID is a two digit string\n$key = "your_unique_key_for_each_installation";\n\n$encryptedPetCode = xorEncrypt($petVariationID, $key);\necho "Encrypted URL code: " . $encryptedPetCode . "\n";\n\n$decryptedPetCode = xorDecrypt($encryptedPetCode, $key);\necho "Decrypted pet variation ID: " . $decryptedPetCode . "\n";