$fo = fopen("files/users.csv", "rb+");\n$data = [];\nwhile (($row = fgetcsv($fo, 0, ';')) !== false) {\n    $data[] = $row;\n}\nfclose($fo);\n\n// Now you can access each row by index\nforeach ($data as $row) {\n    list($name, $street, $country, $accessories, $orderdate) = $row;\n    echo "Name: $name\n";\n    echo "Street: $street\n";\n    echo "Country: $country\n";\n    echo "Accessories: $accessories\n";\n    echo "Order Date: $orderdate\n";\n    echo "\n";\n}