<?php\nfunction getCountryCodeFromLanguageHeader() {\n    if (!empty($_SERVER['HTTP_ACCEPT_LANGUAGE'])) {\n        // Split the string by commas\n        $langs = explode(',', $_SERVER['HTTP_ACCEPT_LANGUAGE']);\n        \n        // Get the first language listed (usually the primary one)\n        $primaryLang = $langs[0];\n        \n        // Extract the country code using a regular expression\n        preg_match('/-([a-z]{2})/i', $primaryLang, $match);\n        \n        if (!empty($match) && isset($match[1])) {\n            return strtolower($match[1]);\n        }\n    }\n    return null; // Return null if no country code is found\n}\n\n$countryCode = getCountryCodeFromLanguageHeader();\necho "Detected Country Code: " . ($countryCode ? $countryCode : 'Unknown');\n?>