<?php\n// Assuming the form data is POSTed\n$day = $_POST['day'] ?? '';\n$month = $_POST['month'] ?? '';\n$year = $_POST['year'] ?? '';\n\n// Validate each part to ensure they are not empty and represent a valid date\nif (!checkdate((int)$month, (int)$day, (int)$year)) {\n    die("Error: Please select a valid date.");\n}\n\n// Format the date as YYYY-MM-DD for MySQL insertion\n$dateOfBirth = sprintf("%04d-%02d-%02d", $year, $month, $day);\n\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database_name";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare and bind\n$stmt = $conn->prepare("INSERT INTO your_table (date_of_birth) VALUES (?)");\n$stmt->bind_param("s", $dateOfBirth);\n\n// Execute the statement\nif ($stmt->execute()) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close connections\n$stmt->close();\n$conn->close();\n?>