<?php\n// Path to your LaTeX file\n$latexFilePath = '/path/to/your/document.tex';\n\n// Command to compile LaTeX to PDF\n$command = "pdflatex -interaction=nonstopmode $latexFilePath";\n\n// Execute the command\n$output = shell_exec($command);\n\n// Optionally, you can check for errors or handle the output\nif ($output === null) {\n    echo "Error executing command.";\n} else {\n    echo "PDF generated successfully.";\n}\n?>