<?php\n$targetDir = "/path/to/non-web-accessible/directory/";\n$fileName = basename($_FILES["fileToUpload"]["name"]);\n$fileType = pathinfo($fileName, PATHINFO_EXTENSION);\n\n// Validate file type (only allow .jpg files as an example)\n$allowedTypes = ['jpg', 'png', 'jpeg'];\nif (!in_array(strtolower($fileType), $allowedTypes)) {\n    die("Invalid file type.");\n}\n\n// Rename the file to prevent script execution\n$newFileName = uniqid() . '.' . $fileType;\n$targetFile = $targetDir . $newFileName;\n\n// Move the file to the target directory\nif (move_uploaded_file($_FILES["fileToUpload"]["tmp_name"], $targetFile)) {\n    echo "The file ". htmlspecialchars($newFileName) . " has been uploaded.";\n} else {\n    echo "Sorry, there was an error uploading your file.";\n}\n?>