// Your binary data stored in a variable\n$bin = b"Your Binary Data Here";\n\n// Open a memory stream and write your binary data to it\n$stream = fopen('php://memory', 'w+b');\nfwrite($stream, $bin);\n\n// Move the pointer using fseek()\nfseek($stream, 10); // Move the position within the string\n\n// Read from the current position onwards\necho fread($stream, 5); // Adjust the length as needed\n\n// Don't forget to close the stream when done\nfclose($stream);