<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $email = filter_var($_POST['email'], FILTER_SANITIZE_EMAIL);\n\n    // Database connection\n    $hostname = 'localhost';\n    $username = 'your_db_username';\n    $password = 'your_db_password';\n    $database = 'your_database_name';\n\n    try {\n        $pdo = new PDO("mysql:host=$hostname;dbname=$database", $username, $password);\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Check if email already exists\n        $stmt = $pdo->prepare('SELECT email FROM subscriptions WHERE email = :email');\n        $stmt->execute(['email' => $email]);\n        if ($stmt->rowCount() == 0) {\n            // Insert new email\n            $stmtp = $pdo->prepare('INSERT INTO subscriptions (email, subscribe_date) VALUES (:email, NOW())');\n            $stmtp->execute(['email' => $email]);\n            echo 'success';\n        } else {\n            echo 'Email already subscribed.';\n        }\n    } catch (PDOException $e) {\n        echo 'Database error: ' . $e->getMessage();\n    }\n}\n?>