// Example of using prepared statements in PHP to handle AJAX data securely\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($stmt = $mysqli->prepare("UPDATE table_name SET content = ? WHERE id = ?")) {\n    // Assuming you have sanitized and validated the inputs $content and $id\n    $stmt->bind_param("si", $content, $id);  // 's' for string, 'i' for integer\n\n    // Sanitize input (basic example)\n    $content = filter_input(INPUT_POST, 'content', FILTER_SANITIZE_STRING);\n    $id = intval(filter_input(INPUT_POST, 'id', FILTER_SANITIZE_NUMBER_INT));\n\n    $stmt->execute();\n    if ($stmt->affected_rows > 0) {\n        echo "Content updated successfully.";\n    } else {\n        echo "Error updating content.";\n    }\n\n    $stmt->close();\n} else {\n    echo "Error preparing statement: " . $mysqli->error;\n}\n\n$mysqli->close();