<?php\n$input = $_GET['user-input']; // Ensure you're sanitizing or using parameters\n\n// Using sqlsrv for SQL Server in PHP\n$serverName = "your_server_name";\n$connectionOptions = array("Database" => "your_database", "Uid" => "your_username", "PWD" => "your_password");\n\n$conn = sqlsrv_connect($serverName, $connectionOptions);\n\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n$userInput = trim($input); // Trim input to remove unnecessary spaces\n\n// Use parameterized query\n$sql = "\n    SELECT f_name, l_name, (f_name + ' ' + l_name) AS full_name\n    FROM users_table\n    WHERE f_name = ?\n       OR l_name = ?\n       OR (f_name + ' ' + l_name) = ?\n";\n\n$params = array($userInput, $userInput, $userInput);\n\n$stmt = sqlsrv_query($conn, $sql, $params);\n\nif ($stmt === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\nwhile ($row = sqlsrv_fetch_array($stmt, SQLSRV_FETCH_ASSOC)) {\n    echo "First Name: " . $row['f_name'] . ", Last Name: " . $row['l_name'] . "<br>";\n}\n\nsqlsrv_free_stmt($stmt);\nsqlsrv_close($conn);\n?>