<?php\n\ninterface Turnable {\n    public function turn();\n}\n\nclass Screwdriver implements Turnable {\n    public function turn() {\n        $this->turnScrewdriver();\n    }\n\n    private function turnScrewdriver() {\n        echo "Turning screwdriver.\n";\n    }\n}\n\nclass SteeringWheel implements Turnable {\n    public function turn() {\n        $this->turnSteeringWheel();\n    }\n\n    private function turnSteeringWheel() {\n        echo "Turning steering wheel.\n";\n    }\n}\n\nfunction operateTurnable(Turnable $item) {\n    $item->turn();\n}\n\n$screwdriver = new Screwdriver();\n$steeringWheel = new SteeringWheel();\n\noperateTurnable($screwdriver); // Outputs: Turning screwdriver.\noperateTurnable($steeringWheel); // Outputs: Turning steering wheel.