class SomeClass {\n    private function someFunction() {\n        echo "someFunction called\n";\n    }\n\n    private function anotherFunction() {\n        echo "anotherFunction called\n";\n    }\n\n    public function __construct() {\n        // Create a dispatch table with callable arrays\n        $this->dispatch = array(\n            1 => [$this, 'someFunction'],\n            2 => [$this, 'anotherFunction']\n        );\n    }\n\n    public function callDispatch($key) {\n        if (isset($this->dispatch[$key])) {\n            // Use call_user_func to invoke the method\n            call_user_func($this->dispatch[$key]);\n        } else {\n            echo "Key not found in dispatch table\n";\n        }\n    }\n}\n\n// Usage example:\n$obj = new SomeClass();\n$obj->callDispatch(1); // Outputs: someFunction called\n$obj->callDispatch(2); // Outputs: anotherFunction called