<?php\n$xmlString = file_get_contents('path/to/your/file.xml');\n\n// Load the XML string into a SimpleXMLElement object\n$xml = simplexml_load_string($xmlString);\n\nif ($xml === false) {\n    echo "Failed loading XML: ";\n    foreach(libxml_get_errors() as $error) {\n        echo "$error\n";\n    }\n} else {\n    // Accessing the value of <subtag1>\n    $info = (string)$xml->subtag1; // Note: Your original XML had a typo, it should be <subtag1> instead of <subtag2>\n    echo "Info: " . htmlspecialchars($info);\n}\n?>