<?php\n// index.php\n\nif (isset($_GET['url'])) {\n    $url = trim($_GET['url'], '/');\n\n    // Split URL by slashes\n    $params = explode('/', $url);\n\n    // The first element is the main part, e.g., 'posts', 'edit'\n    $controller = !empty($params[0]) ? $params[0] : 'defaultController';\n    \n    // The second could be an ID or action\n    $action = isset($params[1]) ? $params[1] : 'index';\n\n    switch ($controller) {\n        case 'posts':\n            if ($action == 'edit') {\n                require 'edit_post.php'; // Assume this handles editing posts\n                break;\n            }\n            require 'view_posts.php'; // Default to viewing posts\n            break;\n\n        default:\n            // Handle other routes or a 404 page\n            http_response_code(404);\n            echo "Page not found.";\n    }\n} else {\n    // Default action when no URL is passed\n    require 'home.php';\n}\n?>