<?php\n\nclass User \n{\n    private $name;\n\n    public function setName($name) \n    {\n        $_SESSION['user_name'] = $name; // Store in session\n        $this->name = $name;\n    }\n\n    public function getName() \n    {\n        if (isset($_SESSION['user_name'])) { // Retrieve from session\n            return $_SESSION['user_name'];\n        }\n        return $this->name; // Fallback to class variable if not set in session\n    }\n}\n\n// Start the session before creating the SOAP server\nsession_start();\n\n$server = new SoapServer(null, array('uri' => 'http://localhost/'));\n$server->setClass('User');\n// Note: PHP's built-in SOAP extension does not support SOAP_PERSISTENCE_SESSION.\n// We manage state manually using sessions.\n$server->handle();\n\n?>