session_start();\n$fileName = $_POST['textFile'];\n\n$url = $_SESSION['serverURL'] . "fileReader.php?textFile=$fileName";\n\n// Set options for the stream context to control the timeout\n$options = [\n    'http' => [\n        'method'  => 'GET',\n        'timeout' => 10 // Timeout in seconds\n    ]\n];\n\n$context  = stream_context_create($options);\n$result = @file_get_contents($url, false, $context);\n\nif ($result === FALSE) {\n    echo "Error retrieving data from the server.";\n} else {\n    echo $result;\n}