class Mysql {\n    private $conn;\n\n    function __construct() {\n        $this->conn = new mysqli(DB_SERVER, DB_USER, DB_PASSWORD, DB_NAME);\n        if ($this->conn->connect_error) {\n            die('There was a problem connecting to the database: ' . $this->conn->connect_error);\n        }\n    }\n\n    function verify_Username_and_Pass($un, $pwd) {\n        $query = "SELECT * FROM user_table WHERE login_username = ? AND login_password = ?";\n\n        if ($stmt = $this->conn->prepare($query)) {\n            $stmt->bind_param('ss', $un, $pwd);\n            $stmt->execute();\n            \n            // Bind result variables\n            $stmt->store_result();\n            \n            if ($stmt->num_rows > 0) {\n                $stmt->close();\n                return true;\n            } else {\n                $stmt->close();\n                return false;\n            }\n        } else {\n            // Handle error in preparing statement\n            echo "Failed to prepare statement: " . $this->conn->error;\n            return false;\n        }\n    }\n}