use Google\Cloud\Vision\V1\ImageAnnotatorClient;\n   use Google\Cloud\Core\ServiceBuilder;\n\n   function checkImageForPornography($imagePath) {\n       $vision = new ImageAnnotatorClient([\n           'projectId' => 'your-project-id',\n           'keyFilePath' => '/path/to/your-service-account-file.json'\n       ]);\n\n       // Read the image file into memory\n       $imageContent = file_get_contents($imagePath);\n       $image = (new \Google\Cloud\Vision\V1\Image())\n           ->setImageContent($imageContent);\n\n       // Perform Safe Search detection on the image\n       $response = $vision->safeSearchDetection($image);\n       $safeSearch = $response->getSafeSearchAnnotation();\n\n       // Interpret the results of the safe search detection\n       switch ($safeSearch->getAdult()) {\n           case \Google\Cloud\Vision\V1\Likelihood::VERY_LIKELY:\n           case \Google\Cloud\Vision\V1\Likelihood::LIKELY:\n               return false;  // Contains adult content\n           default:\n               return true;   // Safe to display\n       }\n\n       $vision->close();\n   }