function fn() {\n    ob_start(); // Start output buffering\n    echo "Direct Output"; // This simulates potential direct output\n    $output = ob_get_contents(); // Capture the output\n    ob_end_clean(); // Clean and end output buffering\n\n    if ($output) {\n        // If there was any direct output, return it\n        return $output;\n    } else {\n        // Otherwise, perform any other logic and return a value\n        return "Return Value";\n    }\n}\n\n// Usage cases\necho fn() . "\n"; // This will output: Return Value\n$var = fn();\necho $var . "\n"; // This will also output: Return Value