function addBusinessDays($startDate, $businessDaysToAdd, $holidays = []) {\n    $date = new DateTime($startDate);\n    $daysAdded = 0;\n\n    while ($daysAdded < $businessDaysToAdd) {\n        $date->modify('+1 day');\n        if ($date->format('N') > 5 || in_array($date->format('Y-m-d'), $holidays)) {\n            continue;\n        }\n        $daysAdded++;\n    }\n\n    return $date->format('l, m/d/Y');\n}\n\n// Example US federal holidays (YYYY-MM-DD format)\n$usHolidays2023 = [\n    '2023-01-02', // New Year's Day\n    '2023-07-04', // Independence Day\n    '2023-12-25'  // Christmas Day\n];\n\necho addBusinessDays('2023-12-05', 3, $usHolidays2023);