// Method (a): Counting rows by thread_id\nfunction getCountByScanning($threadId) {\n    global $pdo;\n    $stmt = $pdo->prepare('SELECT COUNT(*) as count FROM posts WHERE thread_id = :thread_id');\n    $stmt->execute([':thread_id' => $threadId]);\n    return $stmt->fetch()['count'];\n}\n\n// Method (b): Retrieving the stored count\nfunction getCountByStoredValue($threadId) {\n    global $pdo;\n    $stmt = $pdo->prepare('SELECT post_count FROM threads WHERE id = :thread_id');\n    $stmt->execute([':thread_id' => $threadId]);\n    return $stmt->fetch()['post_count'];\n}