$DB->beginTransaction();\n\ntry {\n    $stmt = $DB->prepare("INSERT INTO example (column1) VALUES (:value)");\n    $stmt->execute([':value' => 'test']);\n    \n    // Assume 'id' is the name of your auto-increment column\n    $idQuery = $DB->query("SELECT LAST_INSERT_ID()");\n    $id = $idQuery->fetchColumn();\n    \n    $DB->commit();\n} catch (Exception $e) {\n    $DB->rollBack();\n    throw $e;\n}\n\necho "Inserted ID: " . $id;